import React, { useState, useRef } from 'react';

const ClozeQuestion = ({ question, onChange }) => {
    const [selectedText, setSelectedText] = useState('');
    const contentEditableRef = useRef(null);
    const [answer, setAnswer] = useState('');
    const handleContentChange = () => {
        onChange({
            ...question,
            content: contentEditableRef.current.innerText,
        });
    };
    console.log(answer);
    const handleInsertBlank = () => {
        const contentEditable = contentEditableRef.current;
        // Replace the selected text with a blank
        const modifiedContent = contentEditable.innerText.replace(selectedText, '_____');
        // Set the modified content and update state
        contentEditable.innerText = modifiedContent;
        setAnswer(selectedText);
        handleContentChange();
    };

    const handleSelect = () => {
        const selection = window.getSelection();
        setSelectedText(selection.toString());
    };

    return (
        <div>
            <div>
                <label style={{ marginBottom: '8px', display: 'block' }}>Cloze (Fill in the Blank):</label>
                <div
                    ref={contentEditableRef}
                    contentEditable
                    placeholder="Enter the fill-in-the-blank question"
                    onBlur={handleContentChange}
                    onSelect={handleSelect}
                    style={{
                        border: '1px solid #ccc',
                        padding: '8px',
                        borderRadius: '4px',
                        marginBottom: '10px',
                        minHeight: '50px', // Adjust height as needed
                        overflowY: 'auto',
                    }}
                >
                    {question.content}
                </div>
                <button
                    onClick={handleInsertBlank}
                    style={{
                        backgroundColor: '#4caf50',
                        color: 'white',
                        padding: '10px',
                        border: 'none',
                        borderRadius: '4px',
                        cursor: 'pointer',
                    }}
                >
                    Insert Blank
                </button>
            </div>
        </div>

    );
};

export default ClozeQuestion;
