import React, { useState } from 'react';
import { useDrop, useDrag } from 'react-dnd';

const Answer = ({ answer, index, moveAnswer }) => {
    const [, ref] = useDrag({
        type: 'ANSWER',
        item: { index },
    });

    return (
        <div ref={ref} style={{ padding: '8px', border: '1px solid #ccc', margin: '4px', cursor: 'move' }}>
            {answer.text}
        </div>
    );
};

const Category = ({ category, answers, setAnswers, moveAnswer }) => {
    const [, drop] = useDrop({
        accept: 'ANSWER',
        drop: (item) => {
            moveAnswer(item.index, answers.length);
            const updatedAnswers = [...answers];
            updatedAnswers[item.index].category = category;
            setAnswers(updatedAnswers);
        },
    });

    return (
        <div ref={drop} style={{ padding: '8px', border: '2px solid #000', margin: '8px' }}>
            <strong>{category}</strong>
            {answers
                .filter((answer) => answer.category === category)
                .map((answer, index) => (
                    <p key={index}>{answer.text}</p>
                ))}
        </div>
    );
};

const CategorizeQuestion = ({ question, onChange }) => {
    const [answers, setAnswers] = useState([]);

    const handleAnswerChange = (dragIndex, dropIndex) => {
        const updatedAnswers = [...answers];
        const [draggedAnswer] = updatedAnswers.splice(dragIndex, 1);
        updatedAnswers.splice(dropIndex, 0, draggedAnswer);
        setAnswers(updatedAnswers);
    };

    return (
        <div>
            <label>Categorize:</label>
            <input
                type="text"
                placeholder="Enter the categorization question"
                value={question.content}
                onChange={(e) => onChange({ ...question, content: e.target.value })}
            />
            <div style={{ display: 'flex', marginTop: '10px' }}>
                <div style={{ flex: 1 }}>
                    <h4>Answers</h4>
                    {answers.map((answer, index) => (
                        <Answer key={index} answer={answer} index={index} moveAnswer={handleAnswerChange} />
                    ))}
                </div>
                <div style={{ flex: 1 }}>
                    <h4>Categories</h4>
                    {['Category 1', 'Category 2', 'Category 3'].map((category, index) => (
                        <Category
                            key={index}
                            category={category}
                            answers={answers}
                            setAnswers={setAnswers}
                            moveAnswer={handleAnswerChange}
                        />
                    ))}
                </div>
            </div>
        </div>
    );
};

export default CategorizeQuestion;
